                        DECLARE
-- input vals
attribute_name  varchar2(256) := trim(:I_2);
attribute_value varchar2(64)  := trim(:I_3);
login           varchar2(256) := trim(:I_1);

dt              date := sysdate;
o_err       varchar2(2000);
service_name varchar2(512);

BEGIN 

service_name := beltel_ip_mddrv_utl.get_service_name_by_user_id(login,'beltel.by','IP SUBSCRIBERS');

            :O_2 := Bp_inet_reg.setvalue (
                        'IP SUBSCRIBERS',
                        service_name,
                        attribute_name,
                        attribute_value,
                        o_err,
                        dt
                    );

   IF nvl(:O_2,0) > 0 
   THEN 
      :O_2    := Bp_inet_reg.Applychanges (o_err,TRUE); 
   END IF; 

   IF :O_2 > 0 
   THEN 
          :O_1    := 
                'Executed;ExitCode=1;ResultCode=' 
             || :O_2 
             || ';ErrorDescription=No errors. '||attribute_name||' successfully seted to '||attribute_value; 
       COMMIT; 
   ELSE 
                :O_1    := 
                'Not Executed;ExitCode=20;ResultCode=' 
             || :O_2 
             || ';ErrorDescription=Can not set Attribute '||attribute_name||': '||o_err;
      ROLLBACK; 
   END IF; 

EXCEPTION 
   WHEN OTHERS THEN
                :O_1    := 
                'Not Executed;ExitCode=50;ResultCode=' 
             || SQLCODE 
             || ';ErrorDescription=Exception occured while try to set '||attribute_name
             ||' to '||attribute_value||' .'
             ||SQLERRM||'. '||DBMS_UTILITY.FORMAT_ERROR_BACKTRACE;
             ROLLBACK;
END;


                        DECLARE
-- input vals
attribute_name  varchar2(256) := trim(:I_2);
attribute_value varchar2(64)  := trim(:I_3);
login           varchar2(256) := trim(:I_1);

dt              date := sysdate;
o_err        varchar2(2000);
service_name varchar2(512);

BEGIN 

service_name := beltel_ip_mddrv_utl.get_service_name_by_user_id(login,'beltel.by','IP SUBSCRIBERS');

      :O_2 := bp_inet_reg.deletevalue(i_service$type => 'IP SUBSCRIBERS',
                                     i_service$name => service_name,
                                     i_attribute$name => attribute_name,
                                     i_value => nvl(attribute_value,'0'),
                                     o_err => o_err,
                                     i_td => dt);   

   IF nvl(:O_2,0) > 0 
   THEN 
      :O_2    := Bp_inet_reg.Applychanges (o_err,TRUE); 
   END IF; 

   IF :O_2 > 0 
   THEN 
          :O_1    := 
                'Executed;ExitCode=1;ResultCode=' 
             || :O_2 
             || ';ErrorDescription=No errors. '||attribute_name||' successfully removed with value '||attribute_value; 
       COMMIT; 
   ELSE 
                :O_1    := 
                'Not Executed;ExitCode=20;ResultCode=' 
             || :O_2 
             || ';ErrorDescription=Can not remove adttibute '||attribute_name||': '||o_err;
      ROLLBACK; 
   END IF; 

EXCEPTION 
   WHEN OTHERS THEN
                :O_1    := 
                'Not Executed;ExitCode=50;ResultCode=' 
             || SQLCODE 
             || ';ErrorDescription=Exception occured while try to remove '||attribute_name
             ||' value '||attribute_value||' .'
             ||SQLERRM||'. '||DBMS_UTILITY.FORMAT_ERROR_BACKTRACE;
