                        DECLARE
attribute_name  varchar2(256) := trim(:I_2);
login           varchar2(256) := trim(:I_1);
service_name    varchar2(512);
dt              date := sysdate;
attr_code       number := 0;
attr_exist      boolean;
res             number;
p_code          number;
ret_vat         varchar2(512) := 'NULL';
BEGIN 

service_name := beltel_ip_mddrv_utl.get_service_name_by_user_id(login,'beltel.by','IP SUBSCRIBERS');

attr_exist := k_dicdat.term_exists(803,attribute_name,2,dt,null);

if not attr_exist then
          :O_1    := 
                'Not Executed;ExitCode=20;ResultCode=20' 
             || ';ErrorDescription=Attribute '||attribute_name||' not defined on CBOSSIP;ResponseObject='||ret_vat; 
end if;

res := k_dicdat.codebyterm(803,attribute_name,attr_code,p_code);

if nvl(attr_code,0) > 0  then

select /*+  INDEX(s INET_SRV$NAME$TYPE$TD$FD) #GetAttrValue UF*/ nvl(a.val,'NULL') into ret_vat
   from inet_srv s, inet_srv_attr a where 1=1
and s.name = service_name
and dt between s.fd and s.td
and a.up = s.n
and dt between a.fd and a.td
and a.attr = attr_code;

            :O_1    := 
                'Executed;ExitCode=1'
             ||';ResultCode=1'
             ||';ErrorDescription=No errors;ResponseObject='||ret_vat;

else
            :O_1    := 
                'Not Executed;ExitCode=20'
             ||';ResultCode=20'
             || ';ErrorDescription=Can not get attribute code by name '||attribute_name||';ResponseObject='||ret_vat; 
end if;

EXCEPTION
   WHEN NO_DATA_FOUND then
            :O_1    := 
                'Executed;ExitCode=1'
             ||';ResultCode=0'
             ||';ErrorDescription=Attribute not found on service '||service_name||';ResponseObject='||ret_vat;

   WHEN OTHERS THEN
                :O_1    := 
                'Not Executed;ExitCode=50;ResultCode=' 
             || SQLCODE 
             || ';ErrorDescription=Exception occured while try to get '||attribute_name
             ||' attr_code: '||attr_code
             ||' from service: '||service_name||'. '
             ||SQLERRM||'. '||DBMS_UTILITY.FORMAT_ERROR_BACKTRACE;
             ROLLBACK;
END;


                        declare      
Mobnum varchar2(40):=:I_1;      
Category varchar2(15):=:I_3;
ResultDesc varchar2(2048);      
ResultCode varchar2(20);      
Cursor UserCur  is      
select id from inet_usr       
 where id=Mobnum      
 and sysdate between fd and td;      
UserVar UserCur%rowtype;      
begin      
   k_var.setuserlang(1);      
   OPEN UserCur;      
   FETCH UserCur INTO UserVar;      
   IF (UserCur%FOUND = true)       
   then      
    :O_1 := 'Executed;ExitCode=1;ResultCode=0;ErrorDescription=Subscriber allready exists;';      
    CLOSE UserCur;      
    return;      
   END IF;      
   CLOSE UserCur;      
if Category=8
then
    :O_1 := 'Executed;ExitCode=1;ResultCode=0;ErrorDescription=Subscriber IPTV;';
else
    ResultCode := bp_inet_reg.newUser(Mobnum, '0', 'ACTIVE',ResultDesc, sysdate);
    IF ResultCode > 0  
    THEN
       ResultCode := Bp_inet_reg.Setgroup (:I_1, '0', ResultDesc, sysdate);   
       -- Add new user    
       IF ResultCode > 0 THEN   
           ResultCode := Bp_inet_reg.Newservicexx (   
                :I_1,
                'IP SUBSCRIBERS',
                :I_1 || '@beltel.by',
                'ACTIVE',
                ResultDesc,
                sysdate,
                :I_2);   
           if ResultCode > 0 then            
               :O_1 := 'Executed;ExitCode=1;ResultCode='|| ResultCode|| ';ErrorDescription=No errors;';
               COMMIT;
           else 
               :O_1 := 'Not Executed;ExitCode=20;ResultCode=' || ResultCode || ';ErrorDescription=Problem when executing Newservicexx: ' || ResultDesc || ';';
               ROLLBACK;
           end if;
       else 
           :O_1 := 'Not Executed;ExitCode=20;ResultCode=' || ResultCode || ';ErrorDescription=Problem when executing Setgroup: ' || ResultDesc || ';';
           ROLLBACK;
       end if;
    else 
       :O_1 := 'Not Executed;ExitCode=20;ResultCode=' || ResultCode || ';ErrorDescription=Problem when executing newUser: ' || ResultDesc || ';';
       ROLLBACK;
    end if;
end if;
exception      
 when others then      
         :O_1:='Not Executed;ExitCode=20;ResultCode=' || '0' || ';ErrorDescription=Exception: ' || SQLERRM  || ';';      
end;


                        declare 
sUser varchar2(2048);  
Res number := 1;
Mobnum varchar2(40):=:I_1;

begin       
    Res := bp_inet_reg.terminateUser(Mobnum, :O_1, sysdate-2/86400);

    if Res > 0
    then
        Res := bp_inet_reg.applyChanges(:O_1);
        :O_1 := 'Executed;ExitCode=1;ResultCode=1;ErrorDescription=' || :O_1 || ';';
            commit;
    else
        :O_1 := 'Executed;ExitCode=1;ResultCode=1;ErrorDescription=' || :O_1 || ';';
    end if;    

exception
 when others then
   Res := cb_api.eh(:O_1);
