    rollback;
end;


                        DECLARE    
    mobnum       VARCHAR2 (40)   := :I_1;    
    status       VARCHAR2 (30)   := :I_2;
    resultdesc   VARCHAR2 (4000);    
    resultcode   NUMBER;

    cur_service_name    varchar2(512) := beltel_ip_mddrv_utl.get_service_name_by_user_id(mobnum,'beltel.by','IP SUBSCRIBERS');

    l_d          DATE            := SYSDATE;    
    l_d5         DATE            := SYSDATE+5;    
BEGIN    
    resultcode := bp_inet_reg.setstatus (mobnum, status, resultdesc, l_d);    

    IF resultcode > 0    
    THEN    
        IF status = 'ACTIVE' THEN -- Remove attribute Service-Out-Of-Service by SYSDATE   
            l_d := retdate(cur_service_name);
            resultcode := Bp_inet_reg.Deletevalue (    
                        'IP SUBSCRIBERS',    
                        cur_service_name,    
                        'Service-Out-Of-Service',    
                        '0',    
                        resultdesc,    
                        l_d    
                    );    
        END IF;    
        IF status = 'BLOCKED' THEN -- Add attribute Service-Out-Of-Service by SYSDATE+5    
            resultcode := Bp_inet_reg.Setvalue (    
                        'IP SUBSCRIBERS',    
                        cur_service_name,    
                        'Service-Out-Of-Service',    
                        '0',    
                        resultdesc,    
                        l_d5    
                    );    
        END IF;    
    ELSE    
        :O_1 := 'Not Executed;ExitCode=50;ResultCode=' || resultcode || ';ErrorDescription=' || 'Set status error: ' || resultdesc ||  ';';    
        ROLLBACK;    
        RETURN;    
    END IF;    

    IF resultcode > 0 THEN    
        resultcode := bp_inet_reg.applychanges (resultdesc);    
    ELSE    
        :O_1 := 'Not Executed;ExitCode=50;ResultCode=' || resultcode || ';ErrorDescription=' || 'Set or delete value error: ' || resultdesc ||  ';';    
        ROLLBACK;    
        RETURN;    
    END IF;    

    IF resultcode > 0 THEN    
        COMMIT;    
        :O_1 := 'Executed;ExitCode=1;ResultCode=' || resultcode || ';ErrorDescription=' || resultdesc ||  ';';    
        RETURN;    
    ELSE    
        :O_1 := 'Not Executed;ExitCode=50;ResultCode=' || resultcode || ';ErrorDescription=' || 'Apply changes error: ' || resultdesc ||  ';';    
        ROLLBACK;    
        RETURN;    
    END IF;    
EXCEPTION    
    WHEN OTHERS    
    THEN    
        :O_1 := 'Not Executed;ExitCode=20;ResultCode=NULL;ErrorDescription= EXCEPTION: ' || SQLERRM || ';';    
END;

