IF :O_2 > 0 THEN :O_1 := 'Executed;ExitCode=1;ResultCode=' || :O_2 || ';Response=CBOSSip-00000;ErrorDescription=No errors;'; COMMIT; ELSE :O_1 := 'Executed;ExitCode=20;ResultCode=' || :O_2 || ';ErrorDescription=' || :O_1 || ';'; ROLLBACK; END IF; EXCEPTION WHEN OTHERS THEN :O_1 := 'Not Executed;ExitCode=20;ResultCode=' || SQLCODE || ';ErrorDescription=Exception ' || SQLERRM || ';'; END;


