    if NOT nvl(res,0) > 0 then
			:O_1 := 'Not Executed;ExitCode=50;ResultCode=' || res || ';ErrorDescription= Terminate IP Service error: ' || o_err ||  ';';    
			rollback;
			return;
    end if;

        res := bp_inet_reg.applyChanges(o_err);

        	if NOT nvl(res,0) > 0 then 
			     :O_1 := 'Not Executed;ExitCode=50;ResultCode=' || res || ';ErrorDescription= ApplyChanges error: ' || o_err ||  ';';    
			     rollback;
			     return;
        	end if;    

