                        declare
res                 number;
user_name           varchar2(256) := :I_1;
port_id             varchar2(512) := :I_2;
result_desc         varchar2(2000);

profile_name        varchar2(256);
o_err               varchar2(2000);
cur_service_name    varchar2(512);
user_status         varchar2(32);
l_d5         DATE            := SYSDATE+5;

begin
k_var.setuserlang(1);

cur_service_name := beltel_ip_mddrv_utl.get_service_name_by_user_id(user_name,'beltel.by','IP SUBSCRIBERS');
profile_name     := beltel_ip_mddrv_utl.get_profile_name_by_user_id(user_name,'beltel.by','IP SUBSCRIBERS');
user_status      := beltel_ip_mddrv_utl.get_user_status(user_name);


if  ( (cur_service_name is not null) and (profile_name != 'NONE') and (user_status != 'NONE') ) then

    res := bp_inet_reg.terminateservicex(i_userid => user_name,
                                           i_service$type => 'IP SUBSCRIBERS',
                                           i_service$name => cur_service_name,
                                           o_err => o_err,
                                           i_td => sysdate);



    res := bp_inet_reg.newservicexx( i_user$id => user_name,
                                 i_service$type => 'IP SUBSCRIBERS',
                                 i_service$name => port_id || '@beltel.by',
                                 i_service$status =>  user_status,
                                 O_err            =>  o_err,
                                 i_fd             =>  sysdate + 1/86400 ,
                                 I_PFILE$NAME     =>  profile_name);


   if NOT nvl(res,0) > 0 then
:O_1 := 'Not Executed;ExitCode=20;ResultCode=' || res || ';ErrorDescription=Problem when executing Newservicexx: ' || o_err || ';';
rollback;
return;
   end if;


        IF user_status = 'BLOCKED' THEN -- Add attribute Service-Out-Of-Service by SYSDATE+5    
            res := Bp_inet_reg.Setvalue (    
                        'IP SUBSCRIBERS',    
                        port_id || '@beltel.by',    
                        'Service-Out-Of-Service',    
                        '0',    
                        o_err,    
                        l_d5    
                    );

   if NOT nvl(res,0) > 0 then
:O_1 := 'Not Executed;ExitCode=20;ResultCode=' || res || ';ErrorDescription=Problem when executing Bp_inet_reg.Setvalue: ' || o_err || ';';
rollback;
return;
   end if;
         END IF;   


   if nvl(res,0) > 0 then

        res := bp_inet_reg.applyChanges(o_err);

   if NOT nvl(res,0) > 0 then
    :O_1 := 'Not Executed;ExitCode=20;ResultCode=' || res || ';ErrorDescription=Problem when executing applyChanges: ' || o_err || ';';
    rollback;
    return;
   end if;

        :O_1 := 'Executed;ExitCode=1;ResultCode=' || res || ';ErrorDescription=' || o_err ||  ';';    

   end if;
else
:O_1 := 'Not Executed;ExitCode=20;ResultCode=50;'||
               'ErrorDescription=Can not create IPoE service.'||
               ' Current user ip service defined as '||nvl(cur_service_name,'NULL')||
               ' or service profile defined as '||profile_name||
               ' or user status defined as '||user_status||';';
rollback;
return;
end if;

exception      
 when others then
         rollback;
         :O_1:='Not Executed;ExitCode=20;ResultCode=' || '0' || ';ErrorDescription=Exception: ' || SQLERRM  || ';';    
end;


